<?php

require '../GeminiClient.php';

// Ensure the GEMINI_API_KEY environment variable is set
$apiKey = getenv('GEMINI_API_KEY');
if (!$apiKey) {
    die("Error: GEMINI_API_KEY environment variable not set.\n");
}

$geminiClient = new GeminiClient($apiKey, 'gemini-pro-vision');

// Example Base64 PDF snippet
$base64Pdf = '';
$mimeType = 'application/pdf';

try {
    $prompt = [
        ['type' => 'pdf', 'base64' => $base64Pdf, 'mimeType' => $mimeType],
        ['type' => 'text', 'text' => 'Summarize the main content of this document.']
    ];
    $response = $geminiClient->sendPrompt($prompt);
    echo "Prompt (PDF base64):\n";
    print_r($prompt);
    echo "\nResponse:\n" . $response . "\n\n";
} catch (GeminiApiException $e) {
    echo "Error sending PDF prompt (base64): " . $e->getMessage() . "\n";
} catch (Exception $e) {
    echo "General error: " . $e->getMessage() . "\n";
}